import { useEffect, useState } from 'react';
import "./Dashboard.css";
import Table from 'react-bootstrap/Table';
import axios from 'axios';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import FloatingLabel from 'react-bootstrap/FloatingLabel';
import Form from 'react-bootstrap/Form';

function Example() {
    const [show, setShow] = useState(false);
    const [product, setProduct] = useState([])
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [price, setPrice] = useState("");
    const [variant, setVariant] = useState("");
    const [id, setId] = useState("");

    useEffect(() => {
        getProduct();
    }, [])

    const getProduct = () => {
        axios.get('http://localhost:5002/notes')
            .then((res) => {
                setProduct(res.data.data);
                // console.log(res.data.data);
            })
    }


    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const handleCustomer = (e) => {
        e.preventDefault();

        if (id) {
            axios.put("http://localhost:5002/notes", { id, title, description, variant, price })
                .then((response) => {
                    getProduct("");
                    setId("");
                    setTitle("");
                    setDescription("");
                    setPrice("")


                })


        } else {
            axios.post("http://localhost:5002/notes", { id, title, description, variant, price })
                .then((response) => {
                    getProduct("");
                    setId("");
                    setTitle("");
                    setVariant("");
                    setDescription("");
                    setPrice("")
                    clearForm("");

                })

        }
    }
    const clearForm = () => {
        setId("");
        setTitle("");
        setVariant("");
        setDescription("");
        setPrice("")
    }
    const handleDelete = async (userId) => {
        try {
            const response = await axios.delete('http://localhost:5002/notes', { item: { id: userId } })
            console.log(response.data.data);
            getProduct();
        } catch (error) {
            console.error("There was an error deleting the user!", error);
        }
    };
    const userform = (userData) => {
        setId(userData.id);
        setProduct(userData.product);
        setDescription(userData.description);
        setVariant(userData.variant);
        setPrice(userData.price);
    }

    return (
        <>
            <Button variant="primary" className='btn-1' onClick={handleShow}>
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-plus" viewBox="0 0 16 16">
                    <path d="M8 4a.5.5 0 0 1 .5.5v3h3a.5.5 0 0 1 0 1h-3v3a.5.5 0 0 1-1 0v-3h-3a.5.5 0 0 1 0-1h3v-3A.5.5 0 0 1 8 4" />
                </svg>
            </Button>

            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Add Product</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form onSubmit={handleCustomer}>
                        <FloatingLabel controlId="floatingPassword" label="Product Name" value={title} onChange={(e) => setTitle(e.target.value)}>
                            <Form.Control type="text" className="mb-3" />
                        </FloatingLabel>
                        <FloatingLabel controlId="floatingPassword" label="Variant" value={description} onChange={(e) => setDescription(e.target.value)}>
                            <Form.Control type="text" className="mb-3" />
                        </FloatingLabel>
                        <FloatingLabel controlId="floatingPassword" label="Discription" value={variant} onChange={(e) => setVariant(e.target.value)} >
                            <Form.Control type="text" className="mb-3" />
                        </FloatingLabel>
                        <FloatingLabel controlId="floatingPassword" label="Price" value={price} onChange={(e) => setPrice(e.target.value)}>
                            <Form.Control type="text" className="mb-3" />
                        </FloatingLabel>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" type='submit' className='ms-3' onClick={handleShow}>
                            {id ? 'Update' : 'Add'}
                        </Button>
                    </form>
                </Modal.Body>
                <Modal.Footer>

                </Modal.Footer>
            </Modal >


            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>Product Name</th>
                        <th>Discription</th>
                        <th>Variant</th>
                        <th>Price</th>
                    </tr>
                </thead>
                <tbody>
                    {product.map(item => (
                        <>
                            <tr>
                                <td>{item._id}</td>
                                <td>{item.title}</td>
                                <td>{item.description}</td>
                                <td>{item.variant}</td>
                                <td>{item.price}</td>
                                <Button variant="primary" onClick={() => userform(item.id)} className='mt-2 me-2'>Edit</Button>
                                <Button variant="danger" onClick={() => handleDelete(item.id)} className='mt-2'>Delete</Button>
                            </tr>
                        </>
                    ))}
                </tbody>
            </Table >
        </>
    );
}

export default Example;
