import { useEffect, useState } from 'react';
import "./Dashboard.css";

import Table from 'react-bootstrap/Table';
import axios from 'axios';
import Button from 'react-bootstrap/Button';
// import Modal from 'react-bootstrap/Modal';
// import FloatingLabel from 'react-bootstrap/FloatingLabel';
// import Form from 'react-bootstrap/Form';

function Example() {
    // const [show, setShow] = useState(false);
    const [product, setProduct] = useState([])
    // const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [price, setPrice] = useState("");
    const [variant, setVariant] = useState("");
    const [id, setId] = useState("");

    useEffect(() => {
        getProduct();
    }, [])

    const getProduct = () => {
        axios.get('http://localhost:5002/notes')
            .then((res) => {
                setProduct(res.data.data);
                // console.log(res.data.data);
            })
    }


    // const handleClose = () => setShow(false);
    // const handleShow = () => setShow(true);

    const handleCustomer = (e) => {
        e.preventDefault();

        if (id) {
            axios.put("http://localhost:5002/notes", { id, title, description, variant, price })
                .then((response) => {
                    getProduct("");
                    setId("");
                    setTitle("");
                    setVariant("")
                    setDescription("");
                    setPrice("")


                })


        } else {
            axios.post("http://localhost:5002/notes", { id, title, description, variant, price })
                .then((response) => {
                    getProduct("");
                    setId("");
                    setTitle("");
                    setVariant("");
                    setDescription("");
                    setPrice("")
                    clearForm("");

                })

        }
    }
    const clearForm = () => {
        setId("");
        setTitle("");
        setVariant("");
        setDescription("");
        setPrice("")
    }
    const handleDelete = async (noteId) => {
        try {
            const response = await axios.delete('http://localhost:5002/notes', { data: { id: noteId } })
            console.log(response.data.data);
            getProduct();
        } catch (error) {
            console.error("There was an error deleting the user!", error);
        }
    };
    const userform = (noteData) => {
        setId(noteData.id);
        setProduct(noteData.product);
        setDescription(noteData.description);
        setVariant(noteData.variant);
        setPrice(noteData.price);
    }

    return (
        <>

            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>Product Name</th>
                        <th>Discription</th>
                        <th>Variant</th>
                        <th>Price</th>
                    </tr>
                </thead>
                <tbody>
                    {product.map(data => (
                        <>
                            <tr key={data.id}>
                                <td>{data._id}</td>
                                <td>{data.title}</td>
                                <td>{data.description}</td>
                                <td>{data.variant}</td>
                                <td>{data.price}</td>
                                <Button variant="primary" onClick={() => userform(data)} >Edit</Button>
                                <Button variant="danger" onClick={() => handleDelete(data.id)} >Delete</Button>
                            </tr>
                        </>
                    ))}
                </tbody>
            </Table >
        </>
    );
}

export default Example;
